import Header from './components/Header/Header.jsx';
import CoreConcept from './components/Concept/CoreConcept.jsx';
import { CORE_CONCEPTS } from './data.js';
import TabButton from './components/TabButton/TabButton.jsx';
import { useState } from 'react';

function App() {
	let tabContent = 'Please click a Button.!';
	const [content, setContent] = useState(tabContent);

	function handleSelect(selectedButton) {
		console.log('Hello WORLD.!  selected', selectedButton);
		setContent(selectedButton);
	}
	return (
		<div>
			<Header />
			<main>
				<section id='core-concepts'>
					<h2>Core Concepts</h2>
					<ul id='concepts'>
						<CoreConcept image={CORE_CONCEPTS[0].image} title={CORE_CONCEPTS[0].title} description={CORE_CONCEPTS[0].description} />
						<CoreConcept {...CORE_CONCEPTS[1]} />
						<CoreConcept {...CORE_CONCEPTS[2]} />
						<CoreConcept {...CORE_CONCEPTS[3]} />
					</ul>
				</section>
				<section id='examples'>
					<h2>Examples</h2>
					<menu>
						<TabButton onSelect={() => handleSelect('components')}>Components</TabButton>
						<TabButton onSelect={() => handleSelect('jsx')}>JSX</TabButton>
						<TabButton onSelect={() => handleSelect('props')}>Props</TabButton>
						<TabButton onSelect={() => handleSelect('state')}>State</TabButton>
					</menu>
					{content}
				</section>
			</main>
		</div>
	);
}

export default App;
