import Header from './components/Header/Header.jsx';
import CoreConcept from './components/Concept/CoreConcept.jsx';
import { CORE_CONCEPTS, EXAMPLES } from './data.js';
import TabButton from './components/TabButton/TabButton.jsx';
import { useState } from 'react';

function App() {
	const [selectedTopic, setSelectedTopic] = useState();

	function handleSelect(selectedButton) {
		console.log('Hello WORLD.!  selected', selectedButton);
		setSelectedTopic(selectedButton);
	}
	return (
		<div>
			<Header />
			<main>
				<section id='core-concepts'>
					<h2>Core Concepts</h2>
					<ul id='concepts'>
						<CoreConcept image={CORE_CONCEPTS[0].image} title={CORE_CONCEPTS[0].title} description={CORE_CONCEPTS[0].description} />
						<CoreConcept {...CORE_CONCEPTS[1]} />
						<CoreConcept {...CORE_CONCEPTS[2]} />
						<CoreConcept {...CORE_CONCEPTS[3]} />
					</ul>
				</section>
				<section id='examples'>
					<h2>Examples</h2>
					<menu>
						<TabButton isSelected={selectedTopic === 'components'} onSelect={() => handleSelect('components')}>
							Components
						</TabButton>
						<TabButton isSelected={selectedTopic === 'jsx'} onSelect={() => handleSelect('jsx')}>
							JSX
						</TabButton>
						<TabButton isSelected={selectedTopic === 'props'} onSelect={() => handleSelect('props')}>
							Props
						</TabButton>
						<TabButton isSelected={selectedTopic === 'state'} onSelect={() => handleSelect('state')}>
							State
						</TabButton>
					</menu>
					{!selectedTopic ? (
						<p>Please select a topic.!</p>
					) : (
						<div id='tab-content'>
							<h3>{EXAMPLES[selectedTopic].title}</h3>
							<p>{EXAMPLES[selectedTopic].description}</p>
							<pre>
								<code>{EXAMPLES[selectedTopic].code}</code>
							</pre>
						</div>
					)}
				</section>
			</main>
		</div>
	);
}

export default App;
